package cn.zucc.day03;

public class SmallTest {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		int x=4;int y=2;int z;
        z=x&y;
		System.out.println("4.1"+"\t"+z);
		z=x|y;
		System.out.println("4.2"+"\t"+z);
		z=x^y;
		System.out.println("4.3"+"\t"+z);
		z=x>>y;
		System.out.println("4.4"+"\t"+z);
		z=~x;
		System.out.println("4.5"+"\t"+z);
		z=x<<y;
		System.out.println("4.6"+"\t"+z);
		z=x>>>y;
		System.out.println("4.7"+"\t"+z);
		
		
	}

}
